                left = index+1
                right = len(nums) - 1

                while left < right:
                        currSum = nums[index] + nums[left] + nums[right]
                        if currSum == 0:
                                result.append([nums[index], nums[left], nums[right]])
                                left += 1
                                right -= 1
                        elif currSum < 0:
                                left += 1
                        else:
                                right -= 1
        return  [list(t) for t in set(tuple(element) for element in result)]

