```go
type Queue struct {
  stack1, stack2 *stack.Stack
}

func NewQueue() *Queue {
  return &Queue{
    stack1: stack.New(),
    stack2: stack.New(),
  }
}

func (q *Queue) Enqueue(item interface{}) {
  q.stack1.Push(item)
}

func (q *Queue) Dequeue() interface{} {
  if q.stack2.Len() == 0 {
    for !q.stack1.Empty() {
      q.stack2.Push(q.stack1.Pop())
    }
  }
  return q.stack2.Pop()
}

func (q *Queue) Peek() interface{} {
  if q.stack2.Len() == 0 {
    for !q.stack1.Empty() {
      q.stack2.Push(q.stack1.Pop())
    }
  }
  return q.stack2.Peek()
}

func (q *Queue) Empty() bool {
  return q.stack1.Empty() && q.stack2.Empty()
}
```

