public class ProductOfArrayExceptSelf {

    public static int[] productExceptSelf(int[] nums) {
        int n = nums.length;
        int[] output = new int[n];

        // Calculate the product of all the elements in the array.
        int product = 1;
        for (int i = 0; i < n; i++) {
            product *= nums[i];
