```c++
#include <iostream>
#include <fstream>
#include <vector>

using namespace std;

class VersionControlSystem {
public:
  VersionControlSystem() {}

  void addFile(string filename) {
    // Check if file exists
    ifstream file(filename);
    if (!file.is_open()) {
      cout << "File does not exist: " << filename << endl;
      return;
    }

    // Create a new version
    Version *version = new Version();
    version->filename = filename;
    version->contents = file.readAll();

    // Add the version to the repository
    _versions.push_back(version);
  }

  void commit() {
    // Create a new commit
    Commit *commit = new Commit();
    commit->author = "John Doe";
    commit->message = "Initial commit";

    // Add all of the current versions to the commit
    for (Version *version : _versions) {
      commit->addVersion(version);
    }

    // Save the commit to disk
    commit->save();

    // Clear the current versions
    _versions.clear();
  }

  void checkout(int commitId) {
    // Check if commit exists
    Commit *commit = getCommit(commitId);
    if (commit == nullptr) {
      cout << "Commit does not exist: " << commitId << endl;
      return;
    }

    // Checkout the commit
    for (Version *version : commit->getVersions()) {
      version->checkout();
    }
  }

private:
  struct Version {
    string filename;
    string contents;

    void checkout() {
      // Open the file
      ofstream file(filename);

      // Write the contents to the file
      file << contents;
    }
  };

  struct Commit {
    string author;
    string message;
    vector<Version*> versions;

    void addVersion(Version *version) {
      versions.push_back(version);
    }

    void save() {
      // Save the commit to disk
      ofstream file("commits.txt");
      file << author << endl;
      file << message << endl;

      for (Version *version : versions) {
        file << version->filename << endl;
        file << version->contents << endl;
      }
    }

    Commit* getCommit(int id) {
      // Open the file
      ifstream file("commits.txt");

      // Iterate over all of the commits
      while (file >> author >> message) {
        // Check if the current commit has the specified id
        if (id == getCommitId()) {
          // Return the current commit
          Commit *commit = new Commit();
          commit->author = author;
          commit->message = message;

          // Iterate over all of the versions in the commit
          while (file >> filename) {
            // Read the contents of the version
            file >> contents;

            // Create a new version
            Version *version = new Version();
            version->filename = filename;
            version->contents = contents;

            // Add the version to the commit
            commit->addVersion(version);
          }

          return commit;
        }
      }

      // Return nullptr if the commit does not exist
      return nullptr;
    }

    int getCommitId() {
      // Get the hash of the commit
      string hash = author + message;

      // Convert the hash to an integer
      int id = stoi(hash);

      // Return the id
      return id;
    }
  };

  vector<Version*> _versions;
};
```

