    string name;
    string path;
    vector<Version> versions;
};

class VersionControlSystem {
private:
    vector<File> files;

    File* findFile(string path) {
        for (int i = 0; i < files.size(); i++) {
            if (files[i].path == path) {
                return &files[i];
            }
        }
        return nullptr;
    }

public:
