    void checkoutFile(string path, int version) {
        File* file = findFile(path);

        if (file == nullptr) {
            cout << "File " << path << " not found." << endl;
            return;
        }

        if (version < 0 || version >= file->versions.size()) {
            cout << "Invalid version number." << endl;
            return;
        }

        string filename = file->name + "_" + to_string(version) + ".txt";
        ofstream outfile(filename);
        outfile << file->versions[version].content;
        outfile.close();

        cout << "Version " << version << " of file " << path << " checked out." << endl;
    }

