function binarySearch(array, target) {
  // Set left pointer at beginning of array
  let left = 0;

  // Set right pointer at end of array
  let right = array.length - 1;

  // Keep going until left and right pointer meet
  while (left <= right) {
    // Calculate middle pointer
    let middle = Math.floor((left + right)/2);

    // Check if the target is greater/less than the middle element
    if (target < array[middle]) {
      // If it is, move the right pointer to the left 
      right = middle - 1;
    } else if (target > array[middle]) {
      // If it is, move the left pointer to the right 
      left = middle + 1;
    } else {
      // If it is neither, return the middle pointer 
      return middle;
    }
  }
  // If the target is not found, return -1; 
  return -1;
}

