type Node struct {
	val  interface{}
	next *Node
}

type Stack struct {
	top *Node
}

func (s *Stack) Push(val interface{}) {
	node := &Node{
		val:  val,
		next: s.top,
	}

	s.top = node
}

func (s *Stack) Pop() (interface{}, error) {
	if s.top == nil {
		return nil, errors.New("stack is empty")
	}

	res := s.top.val
	s.top = s.top.next
	return res, nil
}

func (s *Stack) String() string {
	cur := s.top
	strs := make([]string, 0)
	for cur != nil {
		strs = append(strs, fmt.Sprintf("%v", cur.val))
		cur = cur.next
	}

	return strings.Join(strs, "->")
}

type Queue struct {
	st1 Stack
	st2 Stack
}

func (q *Queue) EnQueue(val interface{}) {
	for {
		res, err := q.st1.Pop()
		if err != nil {
			break
		}
		q.st2.Push(res)
	}

	q.st1.Push(val)

	for {
		res, err := q.st2.Pop()
		if err != nil {
			break
		}
		q.st1.Push(res)
	}
}

func (q *Queue) DeQueue() (interface{}, error) {
	return q.st1.Pop()
}

func (q *Queue) String() string {
	return q.st1.String()
}

