public class ProductArray {
    public int[] solution(int[] nums){
        int[] output = new int[nums.length]; // Create output array 

        int product = 1; // Store multiplication of all values before index

        // Prefill first value
        output[0] = product;  

        // Loop through all numbers
        for(int i = 0; i < nums.length; i++){
            product *= nums[i];         // Multiply product value with index value
            output[i] = product;        // Assign product value to output array
