function twoNumberSum(array, targetSum) {
  let result = [];

  // Sort the array in ascending order
  array.sort((a,b)=>a-b);

  // Establish left pointer and right pointer 
  let leftIdx = 0;
  let rightIdx = array.length - 1;

  while (leftIdx < rightIdx) {
    let sum = array[leftIdx] + array[rightIdx];
    if (sum === targetSum) {
      result.push(array[leftIdx], array[rightIdx]);
      break;
    }
    if (sum > targetSum) rightIdx--;
    else leftIdx++;
  }
  return result;
}

