class VersionControlSystem {
	public:
		// Constructor - should take a list of file names
		VersionControlSystem(const std::vector<std::string> &filenames);

		// Commit the current version of all given files
		void commitVersion();

		// Rollback to a particular version
		void rollback(int version);

		// Get the current version of a specific file
		int getVersion(const std::string &filename);

	private:
		// Mapping from file name to a map of version -> file content
		std::map<std::string, std::map<int, std::string> > versions;

		// Current 'head' version.
		int headVersion;
};

VersionControlSystem::VersionControlSystem(const std::vector<std::string> &filenames) {
	headVersion = 0;

	// Create an empty version for each file
	for (std::string filename : filenames) {
		std::map<int, std::string> version;
		version[headVersion] = "";
		versions[filename] = version;
	}
}

void VersionControlSystem::commitVersion() {
	headVersion++;

	// Copy the current version to a new version number
	for (auto &pair : versions) {
		const std::string &filename = pair.first;
		std::map<int, std::string> &fileVersions = pair.second;

		// Get the content of the current version
		std::string content = fileVersions[headVersion - 1];

		// Copy it to the new version number
		fileVersions[headVersion] = content;
	}
}

void VersionControlSystem::rollback(int version) {
	if (version > headVersion) { return; }

	headVersion = version;

	// Update each file to go back to this version
	for (auto &pair : versions) {
		std::map<int, std::string> &fileVersions = pair.second;

		// Find the last version before the requested version
		int prevVersion = 0;
		for (int v = headVersion; v > 0; v--) {
			if (fileVersions.count(v) > 0) {
				prevVersion = v;
				break;
			}
		}

		// Copy the new version to where the requested version should be
		std::string content = fileVersions[prevVersion];
		fileVersions[headVersion] = content;
	}
}

int VersionControlSystem::getVersion(const std::string &filename) {
	if (versions.count(filename) == 0) {
		return -1;
	}
	return headVersion;
}

