public class Queue 
{ 
    static final int MAX = 1000; 
    int front, rear, size; 
    int arr[] = new int[MAX]; 

    // Constructor 
    public Queue() { 
        front = rear = -1; 
        size = 0; 
    } 

    // Enqueue an item to the queue 
    void enqueue(int item) 
    { 
        // Overflow 
        if (rear == MAX - 1) { 
            System.out.println("Queue Overflow"); 
            return; 
        } 

        // If queue is initially empty 
        if (front == -1) 
            front = 0; 

        arr[++rear] = item; 
        size++; 
    } 

    // Dequeue an item from queue 
    int dequeue() 
    { 
        // Underflow 
        if (front == -1) { 
            System.out.println("Queue Underflow"); 
            return Integer.MIN_VALUE; 
        } 

        // Return front element of queue 
        int item = arr[front]; 
        front++; 
        if (front > rear) { 
            front = rear = -1; 
        } 
        size--; 
        return item; 
    } 

}

