function medianOfTwoSortedArrays(A, B) {
  let sortedArray = A.concat(B).sort((a, b) => a - b);
  let middleIndex = Math.floor(sortedArray.length / 2);
  if (sortedArray.length % 2 === 0) {
    return (sortedArray[middleIndex] + sortedArray[middleIndex - 1]) / 2;
