  let currentIndexB = 0;
  while (currentIndexA < m || currentIndexB < n) {
    if (A[currentIndexA] < B[currentIndexB] || currentIndexB >= n) {
      mergedArray.push(A[currentIndexA]);
      currentIndexA++;
    } else {
      mergedArray.push(B[currentIndexB]);
      currentIndexB++;
