    public boolean exist(char[][] board, String word) {
        int m = board.length, n = board[0].length;

        boolean[][] visited = new boolean[m][n];
        for(int i = 0; i < m; i++) {
            for(int j = 0; j < n; j++) {
                if(exists(board, word, 0, visited, i, j))
                    return true;
            }
        }
        return false;
    }

    private boolean exists(char[][] board, String word, int pos, boolean[][] visited, int x, int y) {
        if(pos == word.length())
            return true;

        if(x < 0 || x >= board.length || y < 0 || y >= board[0].length || visited[x][y])
            return false;

        if(board[x][y] != word.charAt(pos))
            return false;

        visited[x][y] = true;
        if(exists(board, word, pos + 1, visited, x + 1, y) || 
            exists(board, word, pos + 1, visited, x - 1, y) ||
            exists(board, word, pos + 1, visited, x, y + 1) ||
            exists(board, word, pos + 1, visited, x, y - 1))  {
            return true;
        }
        visited[x][y] = false;
        return false;
