// to each substring problem based on the provided strings

const isInterleave = (s1, s2, s3) => {
  if (s1.length + s2.length !== s3.length) return false;

  const matrix = new Array(s1.length + 1).fill(null).map(() => new Array(s2.length + 1).fill(false));

  for (let row = 0; row <= s1.length; row++) {
    for (let col = 0; col <= s2.length; col++) {
      // Fill the first row with true because we are constructing s3
      // from the beginning of both s1 and s2
      if (row === 0 && col === 0) {
        matrix[row][col] = true;
      // If col is 0, make it equal to the row minus one so that
      // We can set the first column of the matrix as true if 
      // the character from s3 matches the character from s1
      } else if (col === 0) {
        matrix[row][col] = matrix[row - 1][col] && s1[row - 1] === s3[row + col - 1];
      // Similarly, if row is 0 we can make it equal to the column minus one 
      // and set the first row as true if the character from s3 matches
      // the character of s2 
      } else if (row === 0) {
        matrix[row][col] = matrix[row][col - 1] && s2[col - 1] === s3[row + col - 1];
      // For all other positions, compare the character of s3 to the characters
      // of s1 and s2. If one does not match the other make the value false.
      // Otherwise set it to the OR of the previous row or column value 
      // (which will determine if the character in s3 can be constructed 
      // from the substring of s1 and s2)
      } else {
        matrix[row][col] = (
          (matrix[row - 1][col] && s1[row - 1] === s3[row + col - 1]) 
          || (matrix[row][col - 1] && s2[col - 1] === s3[row + col - 1])
        );
      }
    }
  } 
  return matrix[s1.length][s2.length];
};
