```python
def diagonalSort(mat):
    m, n = len(mat), len(mat[0])

    # create a dictionary to store all diagonals
    dict = {}

    # grouping each element to its corresponding diagonal
    for i in range(m):
        for j in range(n):
            if (i - j) not in dict:
                dict[i - j] = [mat[i][j]]
            else:
                dict[i - j].append(mat[i][j])

    # sort each diagonal
    for k in dict.keys():
        dict[k].sort(reverse = True)

    # fill the matrix with the sorted diagonals
    for i in range(m):
        for j in range(n):
            mat[i][j] = dict[i - j].pop()

    return mat
