#include <vector>
#include <utility>

using namespace std;

// Graph structure
struct Graph {
    vector<vector<int>> adjacencyList;
    int numVertices;

    // Constructor
    Graph(int numVertices) {
        this->numVertices = numVertices;
        adjacencyList.resize(numVertices);
    }

    // Add edge to graph
    void addEdge(int v1, int v2) {
        adjacencyList[v1].push_back(v2);
    }
};

// Dijkstra's algorithm
vector<pair<int, int>> dijkstra(Graph graph, int start) {
    vector<pair<int, int>> path;
    priority_queue<pair<int, int>, vector<pair<int, int>>> queue;

    // Initialize distance map and priority queue
    vector<int> distance(graph.numVertices, INF);
    distance[start] = 0;
    queue.push({0, start});

    while (!queue.empty()) {
        // Dequeue next vertex
        auto [distance, vertex] = queue.top();
        queue.pop();

        // If vertex has not been visited before, mark it as visited and update distance map
        if (distance[vertex] == INF) {
            distance[vertex] = distance;
            queue.push({distance, vertex});

            // Add neighbors of vertex to priority queue
            for (int neighbor : graph.adjacencyList[vertex]) {
                if (distance[neighbor] == INF) {
