function longestCommonPrefix(strings) {
  // If the input array is empty, return an empty string
  if (strings.length === 0) {
    return "";
  }

  // Initialize a variable to store the longest common prefix
  let longestPrefix = "";

  // Loop through the input array and compare each string with the previous string
  for (let i = 0; i < strings.length; i++) {
    const currentString = strings[i];

    // If the current string is empty, return the longest prefix found so far
    if (currentString === "") {
      return longestPrefix;
    }

    // Find the index of the first difference between the current string and the previous string
    const firstDiffIndex = strings[i - 1].lastIndexOf(currentString);

    // If there is no difference, the current string is a prefix of the previous string
    // and we can append it to the longest prefix
    if (firstDiffIndex === -1) {
      longestPrefix += currentString;
    }

    // If there is a difference, the current string is not a prefix of the previous string
    // and we can start a new longest prefix
    else {
      longestPrefix = currentString;
    }
  }

  // Return the longest common prefix found at the end of the loop
  return longestPrefix;
}

