class Node {
  int value;
  Node next;

  public Node(int value) {
    this.value = value;
    this.next = null;
  }
}

Node deleteDuplicates(Node head) {
  // Create a new node to represent the head of the list
  Node newHead = new Node(0);

  // Traverse the list and add only the distinct values
  Node current = head;
  while (current != null) {
    if (!containsValue(current.value, newHead)) {
      newHead.next = current;
      current = current.next;
    }
  }

  return newHead.next;
}

boolean containsValue(int value, Node head) {
  Node current = head;
  while (current != null) {
    if (current.value == value) {
      return true;
    }
    current = current.next;
  }
  return false;
}

