public class LongestCommonSubsequence {
    public static int longestCommonSubsequence(String s1, String s2) {
        int n1 = s1.length();
        int n2 = s2.length();
        int[] dp = new int[n1 + 1];

        dp[0] = 0;
        dp[1] = 1;

        for (int i = 2; i <= n1 && i <= n2; i++) {
            int maxLen = 0;
            for (int j = 1; j <= i; j++) {
                if (s1.charAt(i - j) == s2.charAt(j - 1)) {
                    maxLen = Math.max(maxLen, j + 1);
                }
            }
            dp[i] = maxLen;
        }

        return dp[n1];
    }

