class Queue {
    Stack<Integer> stack1;
    Stack<Integer> stack2;

    public void enqueue(int item) {
        while (stack1.size() == 0 || stack2.size() == 0) {
            stack1.push(item);
            stack2.push(item);
        }
        stack1.pop();
        stack2.pop();
    }

    public int dequeue() {
        while (stack1.size() == 0 || stack2.size() == 0) {
            int item = stack2.pop();
            stack1.push(item);
        }
        while (stack1.size() > 0 && stack2.size() == 0) {
            stack2.push(stack1.pop());
        }
        return stack1.pop();
    }
}
```
