    if {condition}:
        return {return_value}
    elif {condition}:
        return {return_value}
    else:
        for {loop_variable} in range({start}, {end}):
            if {condition}:
                return {return_value}
        return {return_value}
"""

# Define the function name and input parameter
function_name = "is_prime"
input_parameter = "number"

# Define the function return type and input type
return_type = "bool"
input_type = "number"

# Generate the function code
function_code = function_pattern.format(
    function_name=function_name,
    input_parameter=input_parameter,
    input_type=input_type,
    return_type=return_type,
    function_body=is_prime_body,
)

# Print the generated function code
