```c
#include <fstream>
#include <iostream>
#include <sstream>
#include <string>
#include <vector>

std::vector<std::string> get_directory_contents(const std::string& directory) {
    std::vector<std::string> files;
    for (const auto& entry : std::filesystem::directory_iterator(directory)) {
        if (entry.is_regular_file()) {
            files.push_back(entry.path().string().c_str());
        }
    }
    return files;
}

class Repository {
public:
    Repository(const std::string& directory) {
        files_ = get_directory_contents(directory);
    }

    void add(const std::string& file_path) {
        files_.push_back(file_path);
    }

    void commit(const std::string& message) {
        if (files_.empty()) {
            std::cerr << "Cannot commit: no files added." << std::endl;
            return;
        }

        std::ofstream ofs("commit.txt", std::ios::out | std::ios::trunc);
        if (!ofs) {
            std::cerr << "Cannot commit: failed to open output file." << std::endl;
            return;
        }

        std::stringstream ss;
        ss << "Committing: " << files_[0];
        for (int i = 1; i < files_.size(); i++) {
            ss << ", " << files_[i];
        }
        ss << std::endl;
        ss << "Message: " << message << std::endl;

        ofs << ss.str();
        ofs.close();

        for (auto file : files_) {
            if (std::filesystem::remove(file)) {
                std::cerr << "Cannot commit: failed to remove file." << std::endl;
                return;
            }
        }

        files_.clear();
    }

private:
    std::vector<std::string> files_;
};

int main() {
    Repository repo("./repo");
    repo.add("file1.txt");
    repo.add("file2.txt");
    repo.commit("Committing files.");
    return 0;
}
```
