// Function to find the median of two sorted arrays
vector<int> findMedianSortedArrays(int m, int n) {
    vector<int> nums1(m);
    vector<int> nums2(n);
    int left = 0, right = m + n - 1;
    int median = 0;

    while (left <= right) {
        int mid = (left + right) / 2;
        if (nums1[mid] == nums2[mid]) {
            median = nums1[mid];
            left = mid + 1;
            right = mid - 1;
        } else {
            if (nums1[mid] < nums2[mid]) {
                right = mid - 1;
            } else {
                left = mid + 1;
            }
        }
    }
    return median;
}

