```typescript
// Custom sort function for comparing Items by price in ascending order
function sortByPrice(a: Item, b: Item): number {
  if (a.price < b.price) {
    return -1;
  }
  if (a.price > b.price) {
    return 1;
  }
  return 0;
}

// Array of items to search
