    LinkedList(int arr[], int n) {
        head = new Node;
        head->data = arr[0];
        head->next = head; // Initialize head->next to head
        Node* curr = head;
        for (int i = 1; i < n; i++) {
            Node* temp = new Node;
            temp->data = arr[i];
            temp->next = NULL;
            curr->next = temp;
            curr = curr->next;
        }
    }

    ~LinkedList() {
        Node* curr = head;
        while (curr != NULL) {
            delete curr;
            curr = curr->next;
        }
    }

    void print() {
        Node* curr = head;
        while (curr != NULL) {
            cout << curr->data << ' ';
            curr = curr->next;
        }
        cout << endl;
    }
};

