    my_repo.add_file("file2.txt");
    my_repo.add_file("file3.txt");

    // Set the contents of "file1.txt" to "Hello, world!"
    my_repo.set_file_contents(0, "Hello, world!");

    // Check out "file1.txt" to the working directory
    my_repo.checkout_file(0);

    // Edit the contents of "file1.txt" to "Hello, universe!"
    std::string contents("Hello, universe!");
